import React, { useState } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';

const Card = ({ person }) => {
    const [favoris, setFavoris] = useState([]);

    const switchFav = () => {
        setFavoris(favoris + person.name);
        localStorage.setItem('name', JSON.stringify(favoris));
    }

    return (
        <ul className='box'>
            <div className='card'>
                <h2>{person.name}</h2>
                <button onClick={switchFav}><img className='star' src={"./star.png"} /></button>
            </div>
        </ul >);
};

export default Card;
