import React, { useState } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';

const Card = ({ person }) => {
    const [favoris, setFavoris] = useState("true");

    const switchFav = () => {
        if (typeof (localStorage.getItem(person.name)) == 'undefined') {
            localStorage.setItem(person.name, JSON.stringify(favoris));
        } else {
            localStorage.removeItem(person.name);
        }
    }

    return (
        <ul className='box'>
            <div className='card'>
                <h2>{person.name}</h2>
                <button onClick={switchFav}><img className='star' src={"./star.png"} /></button>
            </div>
        </ul >);
};

export default Card;
